import React from 'react';
import AdminForm from './components/AdminForm';
import UserForm from './components/UserForm';
import SideBar from './components/SideBar/SideBar';
import NavBar from './components/NavBar/NavBar';
import { Switch, Route, withRouter } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      id: 100,
    };
  }

  addToList = (item) => {
    item.id = this.state.id;
    let tempList = this.state.list.concat(item);
    let tempId = this.state.id + 1;
    this.setState({
      list: tempList,
      id: tempId,
    });
    this.props.history.push('/');
  };

  removeFromList = (id) => {
    let tempId = parseInt(id, 10);
    let tempList = this.state.list.filter((item) => item.id !== tempId);
    this.setState({
      list: tempList,
    });
  };

  editItem = (item) => {
    let tempList = [];
    for (let i = 0; i < this.state.list.length; i++) {
      if (this.state.list[i].id !== item.id) {
        tempList.push(this.state.list[i]);
      } else {
        tempList.push(item);
      }
    }
    this.setState({
      list: tempList,
    });
  };

  render() {
    return (
      <div>
        <NavBar />
        <hr />
        <Switch>
          <Route exact path='/' render={() => <SideBar />} />
          <Route
            path='/luotunnus'
            render={() => <UserForm addToList={this.addToList} />}
          />
          <Route
            path='/saldo'
            render={() => <AdminForm addToList={this.addToList} />}
          />
          <Route
            path='/kirjaudu'
            render={() => <AdminForm addToList={this.addToList} />}
          />
          <Route
            path='/admin'
            render={() => <AdminForm addToList={this.addToList} />}
          />
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
