import React from 'react';
import { Link } from 'react-router-dom';
import { List } from 'semantic-ui-react';
import classes from './MainNav.module.css';

// MainNav on oma komponenttinsa, sisältää NavBar:in päävalikon.
// Kirjautuneen käyttäjän mukaan (admin/tavallinen) näytetään eri MainNav
const MainNav = () => {
  let isAdmin = false;

  //   TODO: dynaamisempi JSX:n luonti esim. objektin pohjalta

  return (
    <div>
      {isAdmin ? (
        <List>
          <List.Item>
            <Link to='/' className={classes.Admin}>
              Admin
            </Link>
          </List.Item>
          <List.Item>
            <Link to='/kirjauduulos' className={classes.MenuItem}>
              Kirjaudu ulos
            </Link>
          </List.Item>
        </List>
      ) : (
        <List>
          <List.Item>
            <Link to='/saldo'>Saldo</Link>
          </List.Item>
          <List.Item>
            <Link to='/luotunnus'>Luo tunnus</Link>
          </List.Item>
          <List.Item>
            <Link to='/kirjaudu'>Kirjaudu</Link>
          </List.Item>
        </List>
      )}
    </div>
  );
};

export default MainNav;
